package com.company.day2;

public class ComparisonOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//==, !=,>,<,>=,<=
		
		int num1 = 10;
		int num2 = 50;
		
		if (num1==num2) {
			System.out.println("Num1 and num2 are same");
		}else{
			System.out.println("Num1 and num2 are not the same");;
		}
		
		if (num1!=num2) {
			System.out.println("Num1 is not equal to num2");
		}else{
			System.out.println("Num1 is equal to num2");;
		}
		
		if (num1>num2) {
			System.out.println("Num1 is greater than num2");
		}else{
			System.out.println("Num1 is not greater than num2");;
		}
		
		if (num1<num2) {
			System.out.println("Num1 is less than num2");
		}else{
			System.out.println("Num1 is not less than num2");;
		}
		if (num1>=num2) {
			System.out.println("Num1 is greater than or equal to num2");
		}else{
			System.out.println("Num1 is not greater than or equal to num2");;
		}
		
		if (num1<=num2) {
			System.out.println("Num1 is less than or equal to num2");
		}else{
			System.out.println("Num1 is not less than or equal to num2");;
		}
		
		
	}
}
